import React from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import { ListGroup, ListGroupItem } from 'reactstrap';
import { stat } from 'fs';
import Profile_item from './profile_item'
import axios from 'axios'

export default class leaderProfile extends React.Component{
    constructor(props){
        super(props);
        this.state={
            data:"",
            id:"",
            image:"data:image/png;base64,",

        }
    }
    componentWillMount(){
        if(typeof(this.props.location.data)!= "undefined")
        {
        console.log(this.props.location.data.info)
        this.setState({id : this.props.location.data.info});
        }
        console.log("in handel submit")
        axios.get('http://localhost:8000/api/me/leader/',{
            headers:
             {
               "Authorization" : `Bearer ${localStorage.access}`,
               'Accept' : 'application/json',
               'Content-Type': 'application/json'
           }
    }).then(json => {
          console.log("response")
          console.log(json.data)
          this.setState({
              data:json.data
          })
        }).catch(
        console.log("error"));
    
      }

    render(){
        return(
            <dev>
                <img id="profileImage" src={this.state.image} />
                <h1>{this.state.username}</h1>
                <ListGroup id="items">
                    <Profile_item title = "نام" val={this.state.data.first_name}/>
                    <Profile_item  title ="نام خانوادگی" val={this.state.data.lastname }/>
                    <Profile_item title ="سفرنامه" val={this.state.data.itinerary}/>
                    <Profile_item title ="اتوموبیل" val={this.state.data.has_car?"بله":"خیر"}/>
                    <Profile_item title ="ظرفیت ماشین" val={this.state.data.car_capacity}/>
                    <Profile_item title="مدل ماشین" val={this.state.data.car_model}/>
                </ListGroup>
            </dev>
        );
    }
}